//src/pages/home/index.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import SearchBar from "../../components/SearchBar";
import BannerSlider from "../../components/BannerSlider";
import { getCurrentUser } from "../../services/api"; // âœ… token ile kullanÄ±cÄ± bilgisi Ã§eken fonksiyon
import "./HomePage.css";

const exploreDestinations = [
  { name: "Istanbul", image: "/images/istanbul.jpg" },
  { name: "Antalya", image: "/images/antalya.jpg" },
  { name: "Bodrum", image: "/images/Bodrum.jpg" },
];

const HomePage = () => {
  const navigate = useNavigate();
  const [searchPerformed, setSearchPerformed] = useState(false);
  const [user, setUser] = useState(null); // âœ… login olan kullanÄ±cÄ± bilgisi

  useEffect(() => {
    getCurrentUser()
      .then((res) => {
        setUser(res);
        console.log("Aktif kullanÄ±cÄ±:", res);
      })
      .catch((err) => console.log("KullanÄ±cÄ± bilgisi alÄ±namadÄ±:", err.message));
  }, []);

  const handleSearch = () => {
    setSearchPerformed(true);
  };

  const handleExploreClick = (destination) => {
    navigate(`/results?destination=${encodeURIComponent(destination)}`);
  };

  return (
    <div className="home-container">
      <section className="hero-banner">
        <BannerSlider />
      </section>

      {/* ðŸ‘¤ Login olmuÅŸ kullanÄ±cÄ±yÄ± karÅŸÄ±lama */}
      {user && (
        <div className="welcome-user" style={{ margin: "1rem 0", fontSize: "1.1rem" }}>
          ðŸ‘‹ Welcome, <strong>{user.name || user.email}</strong>
        </div>
      )}

      <SearchBar initialValues={{}} onSearch={handleSearch} />

      {!searchPerformed ? (
        <section className="inspire-section">
          <h2>Explore Popular Destinations</h2>
          <div className="promo-grid">
            {exploreDestinations.map((dest) => (
              <div
                key={dest.name}
                className="promo-card"
                onClick={() => handleExploreClick(dest.name)}
              >
                <img
                  src={dest.image}
                  alt={dest.name}
                  className="promo-image"
                  onError={(e) =>
                    (e.target.src =
                      "https://via.placeholder.com/300x200?text=No+Image")
                  }
                />
                <div className="promo-overlay">{dest.name}</div>
              </div>
            ))}
          </div>
        </section>
      ) : (
        <p style={{ marginTop: "2rem" }}>
          Hotel results will appear here (mock)
        </p>
      )}
    </div>
  );
};

export default HomePage;
