// src/components/HotelCard/index.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import './HotelCard.css';

const HotelCard = ({ hotel }) => {
  return (
    <div className="hotel-card">
      <div className="hotel-image">
        <img src={hotel.imageUrl || '/placeholder-hotel.jpg'} alt={hotel.name} />
      </div>
      
      <div className="hotel-info">
        <h3 className="hotel-name">{hotel.name}</h3>
        <div className="hotel-location">{hotel.location}</div>
        
        <div className="hotel-features">
          {hotel.features && hotel.features.map((feature, index) => (
            <span key={index} className="feature-tag">{feature}</span>
          ))}
        </div>
        
        <div className="hotel-rating">
          <span className="rating-score">{hotel.rating}</span>
          <span className="rating-count">({hotel.reviewCount} reviews)</span>
        </div>
      </div>
      
      <div className="hotel-price">
        <div className="price-amount">${hotel.price}</div>
        <div className="price-period">per night</div>
        <Link to={`/hotel/${hotel.id}`} className="view-details-btn">
          View Details
        </Link>
      </div>
    </div>
  );
};

export default HotelCard;
