// src/index.js
import React from 'react';
import ReactDOM from 'react-dom/client';
import { MsalProvider } from '@azure/msal-react';
import { msalInstance } from './msalInstance';
import App from './App';

// 1) İlk etapta basit bir loading gösterebiliriz
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<div>Loading authentication...</div>);

(async () => {
  try {
    // 2) MSAL instance'ını initialize et
    await msalInstance.initialize();  
    // 3) Redirect yanıtı varsa işle
    await msalInstance.handleRedirectPromise();
  } catch (err) {
    console.error('MSAL init/redirect error:', err);
  } finally {
    // 4) Sonunda gerçek uygulamayı render et
    root.render(
      <React.StrictMode>
        <MsalProvider instance={msalInstance}>
          <App />
        </MsalProvider>
      </React.StrictMode>
    );
  }
})();
