// src/msalConfig.js
// frontend tenant id :
// 7cbc58ec-5a5e-4808-b100-5cba8de635a3
export const msalConfig = {
  auth: {
    clientId: "7cbc58ec-5a5e-4808-b100-5cba8de635a3", // frontend App Registration ID
    authority: "https://hotelbookingext.ciamlogin.com/1649cae6-96a4-42a7-9f82-3e339752b193/HotelBookingSignUp", // ✅ CIAM authority
    knownAuthorities: ["hotelbookingext.ciamlogin.com"],
    redirectUri: "http://localhost:3000"
  },
  cache: {
    cacheLocation: "localStorage",
    storeAuthStateInCookie: false
  }
};

export const loginRequest = {
  scopes: ["https://hotelbookingext.onmicrosoft.com/api/access_as_user"]
};
