/* src/index.js */
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { msalInstance } from './msalInstance';
import { MsalProvider } from '@azure/msal-react';

const root = ReactDOM.createRoot(document.getElementById('root'));
// İlk etapta basit bir loading UI göster
root.render(<div>Loading authentication...</div>);

(async () => {
  try {
    // MSAL instance'ını initialize et
    await msalInstance.initialize();
    // Redirect yanıtı varsa işle
    await msalInstance.handleRedirectPromise();
  } catch (err) {
    console.error('MSAL init/redirect error:', err);
  } finally {
    // Sonunda gerçek uygulamayı render et
    root.render(
      <React.StrictMode>
        <MsalProvider instance={msalInstance}>
          <App />
        </MsalProvider>
      </React.StrictMode>
    );
    // Performance ölçümü devam etsin
    reportWebVitals();
  }
})();
