// src/index.js
import React from 'react';
import ReactDOM from 'react-dom/client';
import { MsalProvider } from '@azure/msal-react';
import { msalInstance } from './msalInstance';
import App from './App';

// 1) İlk aşamada basit bir loading UI
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<div>Loading authentication...</div>);

// 2) Redirect cevabını işle
msalInstance.handleRedirectPromise()
  .then(() => {
    // 3) Başarıyla çözüldüyse gerçek app’i render et
    root.render(
      <React.StrictMode>
        <MsalProvider instance={msalInstance}>
          <App />
        </MsalProvider>
      </React.StrictMode>
    );
  })
  .catch((err) => {
    // 4) Hata varsa console’a bas, yine de app’i render et
    console.error('MSAL redirect error:', err);
    root.render(
      <React.StrictMode>
        <MsalProvider instance={msalInstance}>
          <App />
        </MsalProvider>
      </React.StrictMode>
    );
  });
