// src/msalConfig.js
export const msalConfig = {
 auth: {
    clientId: "7cbc58ec-5a5e-4808-b100-5cba8de635a3", // frontend App Registration Client ID
    authority: "https://hotelbookingext.b2clogin.com/hotelbookingext.onmicrosoft.com/HotelBookingSignUp",
knownAuthorities: ["hotelbookingext.b2clogin.com"],
    redirectUri: "http://localhost:3000"
  },
  cache: {
    cacheLocation: "localStorage",
    storeAuthStateInCookie: false
  }
};

export const loginRequest = {
  scopes: ["https://hotelbookingext.onmicrosoft.com/api/access_as_user"]
};
